basic.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: 'Lato', 'Arial', sans-serif;
}

/* HEADINGS */

h1, p {
  color: #fff;
  text-align: center;
  line-height: 1.4;
}

h1 {
  font-size: 2.2rem;
}

h2 {
  color: #000;
  font-size: 1.3rem;
  text-align: center;
  line-height: 1.4;
  margin-bottom: 10px;
}

/* BASIC SETUP */

.page-wrapper {
  width: 100%;
  height: auto;
}

.nav-wrapper {
  width: 100%;
  position: -webkit-sticky; /* Safari */
  position: sticky;
  top: 0;
  background-color: #fff;
}

.grad-bar {
  width: 100%;
  height: 5px;
  background: linear-gradient(-45deg, #EE7752, #E73C7E, #23A6D5, #23D5AB);
  background-size: 400% 400%;
  	-webkit-animation: gradbar 15s ease infinite;
	-moz-animation: gradbar 15s ease infinite;
	animation: gradbar 15s ease infinite;
}

/* NAVIGATION */

.navbar {
  display: grid;
  grid-template-columns: 1fr 3fr;
  align-items: center;
  height: 50px;
  overflow: hidden;
}

.navbar img {
  height: 16px;
  width: auto;
  justify-self: start;
  margin-left: 20px;
}

.navbar ul {
  list-style: none;
  display: grid;
  grid-template-columns: repeat(6,1fr);
  justify-self: end;

}

.nav-item a {
  color: #000;
  font-size: 0.9rem;
  font-weight: 400;
  text-decoration: none;
  transition: color 0.3s ease-out;
}

.nav-item a:hover {
  color: #3498db;
}

/* SECTIONS */

.headline {
  width: 100%;
  height: 50vh;
  min-height: 350px;
  background: linear-gradient(rgba(0,0,0,0.3),rgba(0,0,0,0.7)), url('https://images.unsplash.com/photo-1435224668334-0f82ec57b605?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1yZWxhdGVkfDd8fHxlbnwwfHx8fA%3D%3D&w=1000&q=80');
  background-size: cover;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.features {
  width: 100%;
  height: auto;
  background-color: #f1f1f1;
  display: flex;
  padding: 50px 20px;
  justify-content: space-around;
}

.feature-container {
  flex-basis: 30%;
  margin-top: 10px;
}

.feature-container p {
  color: #000;
  text-align: center;
  line-height: 1.4;
  margin-bottom: 15px;
}

.feature-container img {
  width: 100%;
  margin-bottom: 15px;
}

/* SEARCH FUNCTION */

#search-icon {
  font-size: 0.9rem;
  margin-top: 3px;
  margin-left: 15px;
  transition: color 0.3s ease-out;
}

#search-icon:hover {
  color: #3498db;
  cursor: pointer;
}

.search {
  transform: translate(-35%);
  -webkit-transform: translate(-35%);
  transition: transform 0.7s ease-in-out;
  color: #3498db;
}

.no-search {
  transform: translate(0);
  transition: transform 0.7s ease-in-out;
}

.search-input {
  position: absolute;
  top: -4px;
  right: -125px;
  opacity: 0;
  z-index: -1;
  transition: opacity 0.6s ease;
}

.search-active {
  opacity: 1;
  z-index: 0;
}

input {
  border: 0;
  border-left: 1px solid #ccc;
  border-radius: 0; /* FOR SAFARI */
  outline: 0;
  padding: 5px;
}

/* MOBILE MENU & ANIMATION */

.menu-toggle .bar{
  width: 25px;
  height: 3px;
  background-color: #3f3f3f;
  margin: 5px auto;
  -webkit-transition: all 0.3s ease-in-out;
  -o-transition: all 0.3s ease-in-out;
  transition: all 0.3s ease-in-out;
}

.menu-toggle {
  justify-self: end;
  margin-right: 25px;
  display: none;
}

.menu-toggle:hover{
  cursor: pointer;
}

#mobile-menu.is-active .bar:nth-child(2){
  opacity: 0;
}

#mobile-menu.is-active .bar:nth-child(1){
  -webkit-transform: translateY(8px) rotate(45deg);
  -ms-transform: translateY(8px) rotate(45deg);
  -o-transform: translateY(8px) rotate(45deg);
  transform: translateY(8px) rotate(45deg);
}

#mobile-menu.is-active .bar:nth-child(3){
  -webkit-transform: translateY(-8px) rotate(-45deg);
  -ms-transform: translateY(-8px) rotate(-45deg);
  -o-transform: translateY(-8px) rotate(-45deg);
  transform: translateY(-8px) rotate(-45deg);
}

/* KEYFRAME ANIMATIONS */

@-webkit-keyframes gradbar {
	0% {
		background-position: 0% 50%
	}
	50% {
		background-position: 100% 50%
	}
	100% {
		background-position: 0% 50%
	}
}

@-moz-keyframes gradbar {
	0% {
		background-position: 0% 50%
	}
	50% {
		background-position: 100% 50%
	}
	100% {
		background-position: 0% 50%
	}
}

@keyframes gradbar {
	0% {
		background-position: 0% 50%
	}
	50% {
		background-position: 100% 50%
	}
	100% {
		background-position: 0% 50%
	}
}

/* Media Queries */

  /* Mobile Devices - Phones/Tablets */

@media only screen and (max-width: 720px) { 
  .features {
    flex-direction: column;
    padding: 50px;
  }

  /* MOBILE HEADINGS */

  h1 {
    font-size: 1.9rem;
  }

  h2 {
    font-size: 1rem;
  }

  p {
    font-size: 0.8rem;
  }

  /* MOBILE NAVIGATION */

  .navbar ul {
    display: flex;
    flex-direction: column;
    position: fixed;
    justify-content: start;
    top: 55px;
    background-color: #fff;
    width: 100%;
    height: calc(100vh - 55px);
    transform: translate(-101%);
    text-align: center;
    overflow: hidden;
  }

  .navbar li {
    padding: 15px;
  }

  .navbar li:first-child {
    margin-top: 50px;
  }

  .navbar li a {
    font-size: 1rem;
  }

  .menu-toggle, .bar {
    display: block;
    cursor: pointer;
  }

  .mobile-nav {
  transform: translate(0%)!important;
}

  /* SECTIONS */

  .headline {
    height: 20vh;
  }

  .feature-container p {
    margin-bottom: 25px;
  }

  .feature-container {
    margin-top: 20px;
  }

  .feature-container:nth-child(2) {
    order: -1;
  }

  /* SEARCH DISABLED ON MOBILE */

  #search-icon {
    display: none;
  }

  .search-input {
  display: none;
 }

}

/* 
==================================================
Base Styles
font-family: 'Audiowide', cursive;
font-family: 'Exo 2', sans-serif;
================================================== 
*/

*,
*:before,
*:after {
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
}

* a {
	text-decoration: none;
	font-family: 'Exo 2', sans-serif;
}

body {
	padding: 0;
	margin: 0;
}

h1, h2, h3, h4, h5, h6 {
	font-family: 'Audiowide', cursive;
	letter-spacing: 4px;
}

p {
	line-height: 1.5em;
	letter-spacing: 1px;
	font-family: 'Exo 2', sans-serif;
}

*:focus {
	outline: none;
}

.wrapper {
	padding: 0;
	margin:0;
}

img {
	width: 100%;
	height: auto;
	box-shadow: 0 8px 30px 2px rgba(0, 0, 0, .5);
}

.img-container {
	max-width: 800px;
	height: auto;
}

.about, .s1, .s2, .s3, .s4, .s5, .contact {
	width: 100%;
	height: auto;
	padding: 100px 50px;
	color: #303030;
}

.btn {
	display: inline-block;
	padding: 15px 18px;
	margin: 25px 0;
	border: 1px solid #00CCA0;
	background: transparent;
	color: #00CCA0;
	border-radius: 50px;
	text-transform: uppercase;
	transition: all .35s;
}

.btn:hover {
	background: #00CCA0;
	color: #4CFFD9;
}

/* 
==================================================
Grid Layout
================================================== 
*/

.row {
	display: -webkit-box;
	display: -ms-flexbox;
	display: flex;
	margin: 0 0 20px 0;
}

.col {
	padding: 20px;
}

.row .col {
	-webkit-box-flex: 1;
	-ms-flex: 1;
	flex: 1;
}

.row-halves, .row-thirds, .row-fourths {
	display: -webkit-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-box-pack: justify;
	-ms-flex-pack: justify;
	justify-content: space-between;
}

.row-halves .col {
	width: 49%;
}

.row-thirds .col {
	width: 32%;
}

.row-fourths .col {
	width: 24%;
}

/* 
==================================================
Top Bar
================================================== 
*/

.top-bar {
	position: fixed;
	top: 0;
	z-index: 1; 
	text-align: right;
	padding: 8px 50px;
	width: 100%;
	background: rgba(0, 0, 0, 0.2);
	font-size: .8em;
	border-bottom: 1px solid rgba(0, 0, 0, 0.2);
}

.top-bar ul {
	padding: 0;
	margin: 0;
	list-style: none;
}

.top-bar ul li {
	display: inline;
	padding: 0 10px;
	margin: 0;
	color: red;
}

/* 
==================================================
Standard Nav
================================================== 
*/

.standard-nav {
	position: fixed;
	z-index: 1;
	top: 0px;
	width: 100%;
	background: #222;
	padding: 20px 10px;
	text-align: center;
}

.standard-nav a {
	text-decoration: none;
	color: #eee;
	padding: 0 30px;
	text-transform: uppercase;
	letter-spacing: 0px;
	border-left: 1px solid #424242;
	transition: all .35s;
}

.standard-nav a:last-child {
	border-right: 1px solid #424242;
}

.standard-nav a:hover {
	color: #00CCA0;
}


/* 
==================================================
Mobile Nav
================================================== 
*/

button {
	background: none;
	border: none;
	margin: 20px;
}

button:focus {
	outline: none;
}

.contain {
	position:relative;
	display: inline-block;
	cursor: pointer;
	z-index: 9999;
}

.change .container {
	transform: rotate(90deg);
}

.brand {
	color: #fff;
}

.bar1,
.bar2,
.bar3 {
	width: 25px;
	height: 2px;
	background-color: #fff;
	border-radius: 2px;
	margin: 4px 0;
	transition: 0.4s;
}

.change .bar1 {
	-webkit-transform: rotate(-45deg) translate(-6px, 6.5px);
	transform: rotate(-45deg) translate(-5.5px, 4.5px);
	top: 50px;

	background: #E00049;
}

.change .bar2 {
	position: relative;
	-webkit-transform: translate(30px, 0px); 
	transform: translate(30px, 0px); 
	background: #E00049;
	opacity: 0;
}

.change .bar3 {
	-webkit-transform: rotate(45deg) translate(-7px, -8px);
	transform: rotate(45deg) translate(-4px, -3px);
	background: #E00049;
}


.mobile-navbar {
	position: fixed;
	z-index:3;
	width: 100%;
	height: 58px;
	display: none;
	justify-content: space-between;
	align-content: center;
	align-items: center; 
	padding: 0 40px;
	background-color: #222;	
}

button:focus {
	outline: 0;
}

.open-menu {
	cursor: pointer;
	transition: transform 0.3s;
	color: #fff;
}

.button:hover,
.open-menu:hover {
	transform: scale(1.1);
}

/* Overlay */

.overlay {
	position: fixed;
	z-index: 2;
	background: rgba(0, 0, 0, 0.8);
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	opacity: 0;
	visibility: hidden;
	transition: opacity 0.35s, visibility 0.35s, height 0.35s;
	overflow: auto;
}

.overlay.open {
	opacity: 1;
	visibility: visible;
	height: 100%;
}

.overlay nav {
	position: relative;
	height: 300px;
	top: 300px;
	transform: translateY(-50%);
	text-align: center;
}

.overlay ul {
	list-style: none;
	padding: 0;
	margin: 0 auto;
	display: inline-block;
	position: relative;
	left: 1%;
	height: 100%;
}

.overlay ul li {
	vertical-align: middle;
	display: block;
	position: relative;
	opacity: 0;
	margin-bottom: 35px;
	font-family: "Exo 2", sans-serif;
	font-size: 1.5em;
	font-weight: 400;
	text-transform: uppercase;
	letter-spacing: 4px;
}

.overlay ul li a {
	display: block;
	position: relative;
	color: #fff;
	text-decoration: none;
	overflow: hidden;
	transition: all 0.4s;
}

.overlay ul li a:after {
	content: "";
	position: absolute;
	top: 50%;
	left: 43.5%;
	width: 0%;
	transform: translateX(-50%);
	height: 2px;
	background: #fff;
	transition: 0.35s;
}

.overlay ul li a:hover,
.overlay ul li a:focus,
.overlay ul li a:active {
	color: #fff;
}

.overlay ul li a:hover:after,
.overlay ul li a:focus:after,
.overlay ul li a:active:after {
	width: 100%;
	background: #00CCA0;
}

.overlay.open li {
	animation: fadeInRight 0.5s ease forwards;
	animation-delay: 0.2s;
}

.overlay.open li:nth-of-type(2) {
	animation-delay: 0.25s;
}

.overlay.open li:nth-of-type(3) {
	animation-delay: 0.3s;
}

.overlay.open li:nth-of-type(4) {
	animation-delay: 0.45s;
}

.overlay.open li:nth-of-type(5) {
	animation-delay: 0.5s;
}

/* Animation for menu fade in */

@keyframes fadeInRight {
	0% {
		opacity: 0;
		left: 20%;
	}
	100% {
		opacity: 1;
		left: 0;
	}
}

/* 
==================================================
Hero
================================================== 
*/

.hero {
	width: 100%;
	height: 768px;
	background: url("https://images.unsplash.com/photo-1512286692427-f4798289d26c?ixlib=rb-0.3.5&s=cc29124f2862c18afc92d1e087e702e2&auto=format&fit=crop&w=2378&q=80")no-repeat center;
	background-size: cover;
	text-align: center;
}

.hero header {
	background: rgba(0, 0, 0, 0.5);
	padding: 20px 20px;
	margin: 250px auto;
	max-width: 600px;
	color: #eee;
}

/* 
==================================================
Section 1
================================================== 
*/

.s1 {
	background: #eee;
}

/* 
==================================================
Section 2
================================================== 
*/

.s2 {
	background: #fff;

}

/* 
==================================================
Section 3
================================================== 
*/

.s3 {
	background: #eee;

}

/* 
==================================================
Section 4
================================================== 
*/

.s4 {
	background: #fff;

}

/* 
==================================================
Section 5
================================================== 
*/

.s5 {
	background: #eee;

}

/* 
==================================================
Contact
================================================== 
*/

.contact {
	background: url("https://images.unsplash.com/photo-1518209491056-7ec2a0e71780?ixlib=rb-0.3.5&s=dd8c76e506717d5aa8287e3675b47e7a&auto=format&fit=crop&w=1867&q=80")no-repeat center;
	background-size: cover;
}

.contact h1 {
	color: #eee;
}

label {
	color: #b2b2b2;
}

form input[type="text"],
select,
textarea {
	width: 100%;
	padding: 12px;
  border: 1px solid #b2b2b2;
	background: transparent;
	margin-top: 6px;
	margin-bottom: 16px;
	resize: vertical;
	color: #b2b2b2;
}

form input[type="text"]:focus,
textarea:focus {
	outline: none;
	border: 1px solid #00CCA0;
}

input[type="submit"] {
	background: #00CCA0;
	color: #4CFFD9;
	text-transform: uppercase;
	padding: 12px 25px;
	border: none;
	border-radius: 50px;
	cursor: pointer;
	transition: all 0.35s;
}

input[type="submit"]:hover {
	background-color: #b2b2b2;
	color: #222;
} 

.contact .container {
	margin-top: 20px;
	margin-bottom: 100px;
	background: transparent;
	padding: 20px 0;
}

/* 
==================================================
Footer
================================================== 
*/

footer {
	text-align: center;
	background: #222;
	margin: 0;
}

footer .row {
	margin: 0!important;
}

footer a {
	padding: 0 10px;
	color: #eee;
}

footer ul {
	list-style: none;
	margin-left: 0;
	padding-left:0;
}

footer ul li {
	display: inline;
	padding: 0px 10px;
	color: #eee;
}

 .mobile {
	display: none;
}

/* 
==================================================
Media Queries
================================================== 
*/

@media(max-width: 1024px) {

	.row,
	.row-halves,
	.row-thirds,
	.row-fourths {
		display: block;
	}

	.row .col,
	.row-halves .col,
	.row-thirds .col,
	.row-fourths .col	{
		width: 100%;
		margin: 0;
	}

	.mobile {
		display: block;
	}

	.move {
		display: none;
	}

}

@media (max-width: 768px) {

	.standard-nav {
		display: none;
	}

	.mobile-navbar {
		display: flex;
	}

	.about, .s1, .s2, .s3, .s4, .s5 {
		padding: 100px 0;
	}

	.contact {
		padding: 100px 0 20px 0;
	}

}

@media(max-width: 475px) {
	.hero header {
		margin: 150px auto;
	}
	h1 {
		font-size: 1.5em;
	}
}

