basic.css:
body {
    font: 400 15px Lato, sans-serif;
    line-height: 1.8;
    color: #818181;
}

h2 {
    font-size: 24px;
    text-transform: uppercase;
    color: #303030;
    font-weight: 600;
    margin-bottom: 30px;
}

h4 {
    font-size: 19px;
    line-height: 1.375em;
    color: #303030;
    font-weight: 400;
    margin-bottom: 30px;
}

.jumbotron { 
    background-color: #7C5852; /* #7c5852; /* Orange */
    color: #ffffff;
    padding: 100px 25px;
    font-family: Montserrat, sans-serif;
}

.bg-grey {
    background-color: #f6f6f6;
}

.container-fluid {
    padding: 60px 50px;
}
.logo {
    color: #7c5852;
    font-size: 200px;
}

.logo-small {
    color: #7c5852;
    font-size: 50px;
}

@media screen and (max-width: 768px) {
    .col-sm-4 {
        text-align: center;
        margin: 25px 0;
    }
}

/*** Thumbnail Photos CSS  ***/
.thumbnail {
    padding: 0 0 15px 0;
    border: none;
    border-radius: 0;
}

.thumbnail img {
    width: 100%;
    height: 100%;
    margin-bottom: 10px;
}

/*** Carousel CSS  ***/
.carousel-control.right, .carousel-control.left {
    background-image: none;
    color: #7c5852;
}
.carousel-indicators li {
    border-color: #7c5852;
}
.carousel-indicators li.active {
    background-color: #7c5852;
}
.item h4 {
    font-size: 19px;
    line-height: 1.375em;
    font-weight: 400;
    font-style: italic;
    margin: 70px 0;
}
.item span {
    font-style: normal;
}
/*** End Carousel CSS  ***/

/*** Panels CSS  ***/
.panel {
    border: 1px solid #7c5852; 
    border-radius:0;
    transition: box-shadow 0.5s;
}
.panel:hover {
    box-shadow: 5px 0px 40px rgba(0,0,0, .2);
}
.panel-footer .btn:hover {
    border: 1px solid #7c5852;
    background-color: #fff !important;
    color: #7c5852;
}
.panel-heading {
    color: #fff !important;
    background-color: #7c5852 !important;
    padding: 25px;
    border-bottom: 1px solid transparent;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
}
.panel-footer {
    background-color: #fff !important;
}
.panel-footer h3 {
    font-size: 32px;
}
.panel-footer h4 {
    color: #aaa;
    font-size: 14px;
}
.panel-footer .btn {
    margin: 15px 0;
    background-color: #7c5852;
    color: #fff;
}
/*** Emd Panels CSS  ***/

/*** Navbar CSS  ***/
.navbar {
    margin-bottom: 0;
    background-color: #7c5852;
    z-index: 9999;
    border: 0;
    font-size: 12px !important;
    line-height: 1.42857143 !important;
    letter-spacing: 4px;
    border-radius: 0;
    font-family: Montserrat, sans-serif;
}
.navbar li a, .navbar .navbar-brand {
    color: #fff !important;
}
.navbar-nav li a:hover, .navbar-nav li.active a {
    color: #7c5852 !important;
    background-color: #fff !important;
}
.navbar-default .navbar-toggle {
    border-color: transparent;
    color: #fff !important;
}

/*** end Navbar CSS  ***/

footer .glyphicon {
    font-size: 20px;
    margin-bottom: 20px;
    color: #7c5852;
}

.patrickFace {
    height: auto; 
    width: auto; 
    max-width: 100px; 
    max-height: 100px;
  border-radius: 5px;
}


